const config = {
  pluginSlots: {
    id_verification_page_plugin: {
      plugins: [
        {
          // Insert a custom IDV Page
          op: PLUGIN_OPERATIONS.Insert,
          widget: {
            id: 'id_verification_page_plugin',
            type: DIRECT_PLUGIN,
            RenderWidget: () => (
              <div>
                <p>This is the new IDV page</p>
                <a href="/">Go Home</a>
              </div>
            ),
          },
        },
      ],
    },
  },
};

export default config;

